const CondTempo6 = this.state.condizione7;


export const ImageTempo = () => {
  switch (CondTempo) {
    case "Moderate rain at times":
    case "Moderate or heavy rain shower":
    case "Light rain shower":
    case "Patchy rain possible": {
      return (
        <div>
          <img
            className="weather"
            src="https://i.ibb.co/X5Rb2hN/Combined-Shape.png"
            alt="Temporale"
          />
        </div>
      );
    }
    case "Partly cloudy": {
      return (
        <div>
          <img
            className="oval-1"
            src="https://i.ibb.co/yXJx4XT/Nuvoloso.png"
            alt="Nuvoloso"
          />
        </div>
      );
    }
    case "Clear": {
      return (
        <div>
